
public class sumofarrayvalues {

	public static void main(String args[]) {
		// TODO Auto-generated method stub
		int [] array= {10,20,30,40,50,10};
		int sum = 0;
		//advanced for loop
		for (int num : array) {
			sum = sum+num;
		}
		System.out.println("sum of the array is :"+sum);
		}
				
	}


